// Bootstrap 5.3.0 Bundle - Simplified for static use
(function() {
    'use strict';
    
    // Simple Bootstrap functionality
    window.bootstrap = {
        Modal: {
            getOrCreateInstance: function(element) {
                return {
                    show: function() { element.style.display = 'block'; },
                    hide: function() { element.style.display = 'none'; }
                };
            }
        },
        Collapse: {
            getOrCreateInstance: function(element) {
                return {
                    show: function() { element.classList.add('show'); },
                    hide: function() { element.classList.remove('show'); },
                    toggle: function() { element.classList.toggle('show'); }
                };
            }
        }
    };
    
    // Auto-initialize on DOM ready
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', function() {});
    }
})();