// Simple JavaScript syntax highlighting
(function() {
    if (typeof window === 'undefined') return;
    
    function highlightJS(code) {
        return code
            .replace(/\b(function|var|let|const|if|else|for|while|return|class|extends|import|export|async|await|try|catch|finally|throw|new|this)\b/g, '<span class="token keyword">$1</span>')
            .replace(/("[^"]*"|'[^']*')/g, '<span class="token string">$1</span>')
            .replace(/\/\/.*$/gm, '<span class="token comment">$&</span>')
            .replace(/\/\*[\s\S]*?\*\//g, '<span class="token comment">$&</span>');
    }
    
    window.highlightJavaScript = highlightJS;
})();