// Simple PHP syntax highlighting
(function() {
    if (typeof window === 'undefined') return;
    
    function highlightPHP(code) {
        return code
            .replace(/\b(function|class|if|else|elseif|for|while|return|echo|print|include|require|namespace|use|public|private|protected|static|const|new|this)\b/gi, '<span class="token keyword">$1</span>')
            .replace(/("[^"]*"|'[^']*')/g, '<span class="token string">$1</span>')
            .replace(/\/\/.*$/gm, '<span class="token comment">$&</span>')
            .replace(/\/\*[\s\S]*?\*\//g, '<span class="token comment">$&</span>');
    }
    
    window.highlightPHP = highlightPHP;
})();