// Simple Python syntax highlighting
(function() {
    if (typeof window === 'undefined') return;
    
    function highlightPython(code) {
        return code
            .replace(/\b(def|class|import|from|if|else|elif|for|while|try|except|finally|with|as|return|yield|lambda|and|or|not|in|is|None|True|False)\b/g, '<span class="token keyword">$1</span>')
            .replace(/("[^"]*"|'[^']*'|"""[\s\S]*?""")/g, '<span class="token string">$1</span>')
            .replace(/#.*$/gm, '<span class="token comment">$&</span>');
    }
    
    window.highlightPython = highlightPython;
})();