// Simple syntax highlighter
(function() {
    if (typeof window === 'undefined') return;
    
    function highlightCode() {
        const codeBlocks = document.querySelectorAll('pre code, code[class*="language-"]');
        codeBlocks.forEach(block => {
            let code = block.textContent || block.innerText;
            
            if (block.classList.contains('language-javascript') && window.highlightJavaScript) {
                block.innerHTML = window.highlightJavaScript(code);
            } else if (block.classList.contains('language-python') && window.highlightPython) {
                block.innerHTML = window.highlightPython(code);
            } else if (block.classList.contains('language-php') && window.highlightPHP) {
                block.innerHTML = window.highlightPHP(code);
            }
            
            block.classList.add('hljs');
        });
    }
    
    if (document.readyState === 'loading') {
        document.addEventListener('DOMContentLoaded', highlightCode);
    } else {
        highlightCode();
    }
})();